import React, { useState } from 'react';
import { Link, NavLink, useParams } from "react-router";
import "../../styles/styles.scss";
import RatingAndDistance from "../../components/RatingAndDistance";
import OrderGroupButton from "../../components/Button/OrderGroupButton";
import ScheduleOrderButton from "../../components/Button/ScheduleOrderButton";
import MenuFoodTab from "../../components/Tabs/MenuFoodTab";
import RestaurantCategories from "../../components/Items/RestaurantCategories";
import OrderGroupDialog from "../../components/Dialog/OrderGroupDialog";

const RestaurantPage = () => {
  let { id } = useParams();

  const [open, setOpen] = useState(false);

  const handleClose = () => {
    setOpen(false);
  };

  return (
    <div>
      <div className="container-horizontal">
        <div style={{
          flex: 2
        }}>
          <div className="container-vertical"
            style={{
              minHeight: '270px'
            }}>
            <h1 className="bold-text title">McDonald's® (Adams  Wells)</h1>
            <p className="subtitle">
              Chinese
              Dim Sum
              Noodles
              Thai
              Open until 22:45
              £4.49 delivery
              £7.00 minimum
              1.94 miles away
              107 Lever Street, London, EC1V3RQ
              View map
            </p>
            <RatingAndDistance />
            <div className="container-horizontal">
              <OrderGroupButton onClick={() => setOpen(true)} />
              <ScheduleOrderButton />
            </div>
          </div>
        </div>

        <div className="contain-flex">
          <img
            style={{
              height: '220px',
              width: '100%',
              borderRadius: '5px'
            }}
            src="https://cdn.concreteplayground.com/content/uploads/2020/04/Family-Feast-KFC-supplied.jpg" />
        </div>
      </div>
      <div className="contain-flex">
        <OrderGroupDialog
          open={open}
          onClose={handleClose} />

        <MenuFoodTab />
        <RestaurantCategories />
        <RestaurantCategories />
        <RestaurantCategories />
        <RestaurantCategories />
      </div>
    </div>
  );
}
export default RestaurantPage
