import React, { useContext } from 'react';
import "../../styles/styles.scss";
import AccountButton from '../Button/AccountButton/index';
import CartButton from "../Button/CartButton/index";
import MenuButton from '../Button/MenuButton';
import { ThemeContext } from "../../App.js";
import { Nav } from 'react-bootstrap';

class Header extends React.Component {

  constructor(props) {
    super(props)

    this.state = {
      openLoginDialog : false
    }
  }

  render() {
    return (
      <ThemeContext.Consumer>
        {value =>
          <div
            activeKey="/home"
            className="header-contain">

            <div className="header">
              <Nav.Item className="logo-contain">
                <Nav.Link href="/" className="nav-link">
                  <h3>{value}</h3>
                </Nav.Link>
              </Nav.Item>
              <Nav.Item>
                <Nav.Link href="/menu" className="nav-link">
                  <MenuButton />
                </Nav.Link>
              </Nav.Item>
              <input
                className="input textField"
                placeholder="Tìm sản phẩm, danh mục hay thương hiệu mong muốn ..."
                type="text" />

              <AccountButton
                onClick={() => {
                  this.setState({
                    openGroupOrderDialog: true
                  });
                }}
              />
              <Nav.Item>
                <Nav.Link href="/cart" className="nav-link">
                  <CartButton
                    size="40" />
                </Nav.Link>
              </Nav.Item>

            </div>
          </div>
        }
      </ThemeContext.Consumer>
    );
  }
}

export default Header;
