import React from 'react';
import HomePage from './pages/HomePage';
import './styles/styles.scss';

import BottomFooter from "./components/Footer/BottomFooter/index";
import RestaurantPage from "./pages/RestaurantPage";
import MainContainer from "./pages/Container";
import AuthenticatePage from "./pages/AuthenticatePage";

import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
  useParams,
  useRouteMatch
} from "react-router-dom";

export const ThemeContext = React.createContext('light');

class App extends React.Component {

  constructor(props) {
    super(props)

    this.state = {
      isNotMainPages: true
    }
  }

  yourHandler = () => {
    if (window.location.pathname === "/login/session") {
      this.setState({ isNotMainPages: false });
    }
  }

  logUpdate() {
    alert('Current URL: ' + window.location.pathname);

  }

  render() {
    return (
      <ThemeContext.Provider value='light'>

        <Router onChange={() => {
          if (window.location.pathname === "/login/session") { 
            this.setState({ isNotMainPages: true });
          }
        }}>
          {
            !this.state.isNotMainPages
              ? <ApplicationRouter />
              : <MainContainer>
                {window.location.pathname}
                <ApplicationRouter />
              </MainContainer>
          }
        </Router>
      </ThemeContext.Provider>
    )
  }
}

function ApplicationRouter() {
  return (
    <Switch>
      <Route exact path="/home">
        <HomePage />
      </Route>

      <Route path="/restaurant/:id">
        <RestaurantPage />
      </Route>

      <Route exact path="/login/session">
        <AuthenticatePage />
      </Route>
    </Switch>
  );
}

export default App;
